library(RMeCab)text <-  RMeCabFreq("merosu.txt")# 読み込み、解析。頻度表を作成。head(text)text_temp <- text[text$Info1 =="名詞" & text$Info2 == "一般" |text$Info1 =="動詞" & text$Info2 == "自立" |text$Info1 =="形容詞" & text$Info2 == "自立",]# 抽出できた。抽出条件もひとまず。これで。text_freq <- tail(text_temp[order(text_temp$Freq),],n=30)# トップ30を抽出。tailはどうかと思うから後で変更。text_freq[,1]# 共起度を計測